import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import Detail from './Detail';
import { useGlobalStates } from '../Context/Context';

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context
const Home = () => {
  const{cards, setCards, dentistaSel, setDentistaSel} = useGlobalStates();
  const url = 'https://jsonplaceholder.typicode.com/users'
  useEffect(() => {
      fetch(url)
      .then(cards => cards.json())
      .then(data => setCards(data))
  }, [])

  return (
    <main className="" >
      <h1>Home</h1>
      <div className='card-grid'>
      {cards.map(card =>
      <Link to = {"/dentista/" + card.id} key={card.id}>
        <botton onClick={()=> {setDentistaSel(card)}} >
            <div>
                <img src="./images/doctor.jpg" alt="" style={{width: 500, height: 250, objectFit: 'cover'}}/>
                <h3>{card.name}</h3>
                <h4>{card.username}</h4>
            </div>
            </botton>
            {dentistaSel && <Detail dentistaSel={dentistaSel}/>}
            </Link>
      )}
        </div>
    </main>
  )
}

export default Home
