  const{dentistaSel} =useGlobalStates();
  const params = useParams(); 
  const id = params.id; 

  const url = 'https://jsonplaceholder.typicode.com/users/' + {id};
  useEffect(() => {
      fetch(url)
      .then(cards => cards.json())
      .then(data => setCards(data))
  }, [])


